mapper.select:
select
  follow_id, emp_id, follow_type, follow_content, follow_time, addtime
from CRM_Project.follow


        and follow_id = #{followId}


        and emp_id = #{empId}


        and follow_type = #{followType}


        and follow_content = #{followContent}


        and follow_time = #{followTime}


        and addtime = #{addtime}
